select '2014-10-23', a.*, b.*, c.* from (select count(*) as 'Total' from nova_device where createdAt < '2014-10-23 23:59:59' and createdAt > '2014-10-23') as a, (select count(*) as 'iOS' from nova_device where type = 0 and  createdAt < '2014-10-23 23:59:59' and createdAt > '2014-10-23') as b, (select count(*) as 'Android' from nova_device where type = 1 and  createdAt < '2014-10-23 23:59:59' and createdAt > '2014-10-23') as c;
select '2014-10-24', a.*, b.*, c.* from (select count(*) as 'Total' from nova_device where createdAt < '2014-10-24 23:59:59' and createdAt > '2014-10-24') as a, (select count(*) as 'iOS' from nova_device where type = 0 and  createdAt < '2014-10-24 23:59:59' and createdAt > '2014-10-24') as b, (select count(*) as 'Android' from nova_device where type = 1 and  createdAt < '2014-10-24 23:59:59' and createdAt > '2014-10-24') as c;
